INSERT INTO `voc_art` VALUES (24,14,NULL,'新丰职中采茶戏社团备战韶关市传统文化国庆巡游展',NULL,1486656000,'',NULL,NULL,NULL,'新丰职中2015年3月被县委宣传部、县文广新局授予“粤北采茶传承基地”开始，就组建了一支由舞蹈基本功强，素质较高的师生组成的采茶戏社团，并与每天活动课进行训练。','<p style=\"margin-top: 0px; margin-bottom: 0.2rem; padding: 0px; border: 0px; font-family: Arial, Tahoma, sans-serif; font-size: 0.75rem; font-stretch: inherit; line-height: 1.25rem; vertical-align: baseline; outline: none; box-sizing: border-box; color: rgb(51, 51, 51); white-space: normal; widows: 1; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font-family: 宋体; font-size: 14px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; outline: none; box-sizing: border-box;\">&nbsp;&nbsp;新丰职中采茶戏社团备战韶关市传统文化国庆巡游展</span></p><p style=\"margin-top: 0px; margin-bottom: 0.2rem; padding: 0px; border: 0px; font-family: Arial, Tahoma, sans-serif; font-size: 0.75rem; font-stretch: inherit; line-height: 1.25rem; vertical-align: baseline; outline: none; box-sizing: border-box; color: rgb(51, 51, 51); white-space: normal; widows: 1; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font-family: 宋体; font-size: 14px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; outline: none; box-sizing: border-box;\"></span></p><p style=\"margin-top: 0px; margin-bottom: 0.2rem; padding: 0px; border: 0px; font-family: Arial, Tahoma, sans-serif; font-size: 0.75rem; font-stretch: inherit; line-height: 33px; vertical-align: baseline; outline: none; box-sizing: border-box; color: rgb(51, 51, 51); white-space: normal; widows: 1; text-indent: 28px; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font-family: 宋体; font-size: 14px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; outline: none; box-sizing: border-box;\">新丰职中2015<span style=\"margin: 0px; padding: 0px; border: 0px; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; outline: none; box-sizing: border-box;\">年3</span><span style=\"margin: 0px; padding: 0px; border: 0px; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; outline: none; box-sizing: border-box;\">月被县委宣传部、县文广新局授予“粤北采茶传承基地”开始，就组建了一支由舞蹈基本功强，素质较高的师生组成的采茶戏社团，并与每天活动课进行训练。</span></span></p><p style=\"margin-top: 0px; margin-bottom: 0.2rem; padding: 0px; border: 0px; font-family: Arial, Tahoma, sans-serif; font-size: 0.75rem; font-stretch: inherit; line-height: 33px; vertical-align: baseline; outline: none; box-sizing: border-box; color: rgb(51, 51, 51); white-space: normal; widows: 1; text-indent: 35px; text-align: justify;\"><span style=\"margin: 0px; padding: 0px; border: 0px; font-family: 宋体; font-size: 14px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; outline: none; box-sizing: border-box;\">2015<span style=\"margin: 0px; padding: 0px; border: 0px; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; outline: none; box-sizing: border-box;\">年9</span><span style=\"margin: 0px; padding: 0px; border: 0px; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; outline: none; box-sizing: border-box;\">月15</span><span style=\"margin: 0px; padding: 0px; border: 0px; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: inherit; vertical-align: baseline; outline